import "core-js/modules/es.regexp.flags.js";
import L from "leaflet";
export default {
  name: 'App',
  data() {
    return {
      meals: []
    };
  },
  mounted() {
    this.initMap();
    this.fetchPastaMeals();
  },
  methods: {
    initMap() {
      this.map = L.map('map').setView([0, 0], 2);
      L.tileLayer('https://tile.jawg.io/jawg-light/{z}/{x}/{y}.png?access-token={accessToken}', {
        attribution: 'Map data &copy; <a href="https://www.jawg.io/">Jawg</a> | &copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
        maxZoom: 18,
        accessToken: 'MsXnKTMtfJ4dhKXBU3PqU8FerMf6QB2aMA5Aidw6tLH85H2044dD1FoAtOLlhYey'
      }).addTo(this.map);
    },
    async fetchPastaMeals() {
      try {
        const response = await fetch('https://www.themealdb.com/api/json/v1/1/filter.php?c=Pasta');
        const mealsData = await response.json();
        this.meals = mealsData.meals;
        this.displayMeals();
      } catch (error) {
        console.error('Error fetching pasta meals:', error);
      }
    },
    async displayMeals() {
      for (const meal of this.meals) {
        const mealName = meal.strMeal;
        const mealImage = meal.strMealThumb;
        const mealCountry = meal.strArea;
        const countryCoordinates = await this.fetchCountryCoordinates(mealCountry);
        const mealLocation = [countryCoordinates.latlng[0], countryCoordinates.latlng[1]];
        const markerIcon = L.icon({
          iconUrl: mealImage,
          iconSize: [40, 40],
          iconAnchor: [20, 40],
          popupAnchor: [0, -40]
        });
        L.marker(mealLocation, {
          icon: markerIcon
        }).addTo(this.map).bindPopup(`<b>${mealName}</b><br><img src="${mealImage}" alt="${mealName}" style="max-width: 100px;">`);
      }
    },
    async fetchCountryInfo(countryName) {
      try {
        const response = await fetch(`https://restcountries.com/v3/name/${countryName}?fullText=true`);
        const data = await response.json();
        if (data && data.length > 0) {
          const {
            flags,
            latlng
          } = data[0];
          return {
            flag: flags.png,
            latlng
          };
        } else {
          throw new Error('Country info not found or invalid response');
        }
      } catch (error) {
        console.error('Error fetching country info:', error);
        return {
          flag: '',
          latlng: [0, 0]
        };
      }
    },
    async displayMeals() {
      for (const meal of this.meals) {
        const mealName = meal.strMeal;
        const mealImage = meal.strMealThumb;
        const mealCountry = meal.strArea;
        const countryInfo = await this.fetchCountryInfo(mealCountry);
        const mealLocation = [countryInfo.latlng[0], countryInfo.latlng[1]];
        const markerIcon = L.icon({
          iconUrl: countryInfo.flag,
          // Use the country flag as the marker icon
          iconSize: [40, 40],
          iconAnchor: [20, 40],
          popupAnchor: [0, -40]
        });
        L.marker(mealLocation, {
          icon: markerIcon
        }).addTo(this.map).bindPopup(`<b>${mealName}</b><br><img src="${mealImage}" alt="${mealName}" style="max-width: 100px;">`);
      }
    }
  }
};
