async function fetchPlates() {
  const plates = [];

  // Fetching plates data
  let nextPage = 'https://worldfood.guide/api/photos/';
  while (plates.length < 1000 && nextPage) {
    const response = await fetch(nextPage);
    const data = await response.json();
    nextPage = data.next;
    data.results.forEach(result => {
      const plateName = result.dish_name;
      const plateImage = result.location;
      plates.push({
        name: plateName,
        image: plateImage
      });
    });
  }

  // Fetching details for each plate
  for (const plate of plates) {
    const response = await fetchPlateDetails(plate.name);
    plate.location = response.location; // Assuming the location is fetched from the details API
  }
  return plates;
}
async function fetchPlateDetails(plateName) {
  const response = await fetch(`https://worldfood.guide/api/plate-details/?name=${encodeURIComponent(plateName)}`);
  return await response.json();
}
